import React, { useState } from 'react';
import { Upload, FileText, DollarSign, TrendingUp, AlertTriangle, Download, Calculator, BarChart3, Target, Clock, Shield } from 'lucide-react';

const InvestorDashboard = () => {
  const [formData, setFormData] = useState({
    profileText: '',
    transactionsFile: null,
    paymentsFile: null
  });
  const [results, setResults] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleFileChange = (field, file) => {
    setFormData(prev => ({
      ...prev,
      [field]: file
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.profileText || !formData.transactionsFile) {
      setError('Please provide company profile and transactions file');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const formDataObj = new FormData();
      formDataObj.append('profile_text', formData.profileText);
      formDataObj.append('transactions_file', formData.transactionsFile);
      if (formData.paymentsFile) {
        formDataObj.append('payments_file', formData.paymentsFile);
      }

      const response = await fetch('http://localhost:8000/investor_opportunity', {
        method: 'POST',
        body: formDataObj
      });

      const data = await response.json();

      if (data.error) {
        setError(data.error + (data.detail ? ': ' + data.detail : ''));
      } else {
        setResults(data);
      }
    } catch (err) {
      setError('Failed to analyze opportunity: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  const downloadReport = async () => {
    try {
      const response = await fetch('http://localhost:8000/download_report', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(results)
      });

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.style.display = 'none';
      a.href = url;
      a.download = 'investment_report.pdf';
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);
    } catch (err) {
      setError('Failed to download report: ' + err.message);
    }
  };

  const formatCurrency = (value) => {
    if (value === null || value === undefined) return 'N/A';
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0
    }).format(value);
  };

  const formatPercent = (value) => {
    if (value === null || value === undefined) return 'N/A';
    return `${value.toFixed(1)}%`;
  };

  const getRiskColor = (risk) => {
    switch (risk?.toLowerCase()) {
      case 'low': return 'text-green-600 bg-green-50 border-green-200';
      case 'medium': return 'text-yellow-600 bg-yellow-50 border-yellow-200';
      case 'high': return 'text-red-600 bg-red-50 border-red-200';
      default: return 'text-gray-600 bg-gray-50 border-gray-200';
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-blue-50">
      <div className="container mx-auto px-4 py-8">
        {/* Header */}
        <div className="text-center mb-12">
          <h1 className="text-4xl font-bold text-gray-900 mb-4">
            <Calculator className="inline-block w-10 h-10 mr-3 text-blue-600" />
            Investor Opportunity Engine
          </h1>
          <p className="text-xl text-gray-600 max-w-2xl mx-auto">
            Advanced AI-powered investment analysis and opportunity assessment platform
          </p>
        </div>

        {/* Input Form */}
        <div className="bg-white rounded-2xl shadow-xl border border-gray-200 mb-8 overflow-hidden">
          <div className="bg-gradient-to-r from-blue-600 to-purple-600 px-8 py-6">
            <h2 className="text-2xl font-bold text-white flex items-center">
              <FileText className="w-6 h-6 mr-3" />
              Investment Analysis Request
            </h2>
          </div>

          <form onSubmit={handleSubmit} className="p-8">
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
              <div className="lg:col-span-2">
                <label className="block text-sm font-semibold text-gray-700 mb-3">
                  Company Profile & Description
                </label>
                <textarea
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none transition-all"
                  rows="6"
                  placeholder="Describe the company, business model, market opportunity, team, and any other relevant details..."
                  value={formData.profileText}
                  onChange={(e) => setFormData(prev => ({ ...prev, profileText: e.target.value }))}
                />
              </div>

              <div>
                <label className="block text-sm font-semibold text-gray-700 mb-3">
                  <span className="text-red-500">*</span> Transactions File
                </label>
                <div className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center hover:border-blue-400 transition-colors">
                  <Upload className="w-8 h-8 text-gray-400 mx-auto mb-2" />
                  <input
                    type="file"
                    accept=".csv,.xlsx,.xls"
                    onChange={(e) => handleFileChange('transactionsFile', e.target.files[0])}
                    className="hidden"
                    id="transactions"
                  />
                  <label htmlFor="transactions" className="cursor-pointer">
                    <span className="text-blue-600 font-medium">Upload CSV or Excel</span>
                    <p className="text-xs text-gray-500 mt-1">Must contain: date, revenue, cost columns</p>
                  </label>
                  {formData.transactionsFile && (
                    <p className="text-sm text-green-600 mt-2"> {formData.transactionsFile.name}</p>
                  )}
                </div>
              </div>

              <div>
                <label className="block text-sm font-semibold text-gray-700 mb-3">
                  Payments File (Optional)
                </label>
                <div className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center hover:border-blue-400 transition-colors">
                  <Upload className="w-8 h-8 text-gray-400 mx-auto mb-2" />
                  <input
                    type="file"
                    accept=".csv,.xlsx,.xls"
                    onChange={(e) => handleFileChange('paymentsFile', e.target.files[0])}
                    className="hidden"
                    id="payments"
                  />
                  <label htmlFor="payments" className="cursor-pointer">
                    <span className="text-blue-600 font-medium">Upload CSV or Excel</span>
                    <p className="text-xs text-gray-500 mt-1">Should contain: amount_paid column</p>
                  </label>
                  {formData.paymentsFile && (
                    <p className="text-sm text-green-600 mt-2"> {formData.paymentsFile.name}</p>
                  )}
                </div>
              </div>
            </div>

            {error && (
              <div className="mt-6 p-4 bg-red-50 border border-red-200 rounded-lg">
                <div className="flex items-center">
                  <AlertTriangle className="w-5 h-5 text-red-600 mr-2" />
                  <span className="text-red-700">{error}</span>
                </div>
              </div>
            )}

            <div className="mt-8">
              <button
                type="submit"
                disabled={loading}
                className="w-full bg-gradient-to-r from-blue-600 to-purple-600 text-white py-4 px-8 rounded-lg font-semibold text-lg hover:from-blue-700 hover:to-purple-700 focus:ring-4 focus:ring-blue-300 disabled:opacity-50 disabled:cursor-not-allowed transition-all transform hover:scale-[1.02]"
              >
                {loading ? (
                  <div className="flex items-center justify-center">
                    <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-white mr-3"></div>
                    Analyzing Investment Opportunity...
                  </div>
                ) : (
                  <div className="flex items-center justify-center">
                    <BarChart3 className="w-6 h-6 mr-3" />
                    Analyze Investment Opportunity
                  </div>
                )}
              </button>
            </div>
          </form>
        </div>

        {/* Results */}
        {results && (
          <div className="space-y-8">
            {/* Key Metrics Cards */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
              <div className="bg-white rounded-xl shadow-lg border border-gray-200 p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-gray-600">Total Revenue</p>
                    <p className="text-2xl font-bold text-gray-900">{formatCurrency(results.metrics?.total_revenue)}</p>
                  </div>
                  <DollarSign className="w-8 h-8 text-green-500" />
                </div>
              </div>

              <div className="bg-white rounded-xl shadow-lg border border-gray-200 p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-gray-600">Net Profit</p>
                    <p className="text-2xl font-bold text-gray-900">{formatCurrency(results.metrics?.net_profit)}</p>
                  </div>
                  <Target className="w-8 h-8 text-blue-500" />
                </div>
              </div>

              <div className="bg-white rounded-xl shadow-lg border border-gray-200 p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-gray-600">Growth Rate</p>
                    <p className="text-2xl font-bold text-gray-900">{formatPercent(results.metrics?.growth_rate_percent)}</p>
                  </div>
                  <TrendingUp className="w-8 h-8 text-purple-500" />
                </div>
              </div>

              <div className="bg-white rounded-xl shadow-lg border border-gray-200 p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-gray-600">Data Period</p>
                    <p className="text-2xl font-bold text-gray-900">{results.metrics?.months_of_data} mo</p>
                  </div>
                  <Clock className="w-8 h-8 text-orange-500" />
                </div>
              </div>
            </div>

            {/* Valuation and Investment Recommendation */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
              <div className="bg-white rounded-xl shadow-lg border border-gray-200 p-8">
                <h3 className="text-xl font-bold text-gray-900 mb-6 flex items-center">
                  <Calculator className="w-6 h-6 mr-3 text-blue-600" />
                  Valuation & Investment Range
                </h3>
                <div className="space-y-4">
                  <div className="flex justify-between items-center py-3 border-b border-gray-100">
                    <span className="text-gray-600">Estimated Valuation</span>
                    <span className="font-bold text-lg">{formatCurrency(results.valuation_estimate)}</span>
                  </div>
                  <div className="flex justify-between items-center py-3 border-b border-gray-100">
                    <span className="text-gray-600">Investment Range (Min)</span>
                    <span className="font-semibold">{formatCurrency(results.recommended_investment_range?.min)}</span>
                  </div>
                  <div className="flex justify-between items-center py-3 border-b border-gray-100">
                    <span className="text-gray-600">Investment Range (Max)</span>
                    <span className="font-semibold">{formatCurrency(results.recommended_investment_range?.max)}</span>
                  </div>
                  <div className="flex justify-between items-center py-3">
                    <span className="text-gray-600">Annualized Revenue</span>
                    <span className="font-semibold">{formatCurrency(results.annualized_revenue_estimate)}</span>
                  </div>
                </div>
              </div>

              <div className="bg-white rounded-xl shadow-lg border border-gray-200 p-8">
                <h3 className="text-xl font-bold text-gray-900 mb-6 flex items-center">
                  <Shield className="w-6 h-6 mr-3 text-red-500" />
                  Risk Assessment
                </h3>
                <div className="space-y-4">
                  <div className={`inline-flex px-4 py-2 rounded-full border font-semibold ${getRiskColor(results.risk?.risk_score)}`}>
                    {results.risk?.risk_score || 'Unknown'} Risk
                  </div>
                  <div className="mt-4">
                    <p className="text-sm text-gray-600 mb-2">Risk Factors:</p>
                    <ul className="space-y-2">
                      {results.risk?.drivers?.map((driver, idx) => (
                        <li key={idx} className="flex items-start text-sm">
                          <span className="w-2 h-2 bg-gray-400 rounded-full mt-2 mr-3 flex-shrink-0"></span>
                          {driver}
                        </li>
                      ))}
                    </ul>
                  </div>
                </div>
              </div>
            </div>

            {/* ROI Projections */}
            <div className="bg-white rounded-xl shadow-lg border border-gray-200 p-8">
              <h3 className="text-xl font-bold text-gray-900 mb-6 flex items-center">
                <TrendingUp className="w-6 h-6 mr-3 text-green-600" />
                ROI Projections
              </h3>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                <div>
                  <h4 className="font-semibold text-gray-700 mb-4">Baseline ROI</h4>
                  <div className="space-y-3">
                    <div className="flex justify-between">
                      <span className="text-gray-600">Min Investment ROI</span>
                      <span className="font-semibold">{formatPercent(results.roi_projections?.baseline?.roi_percent_min_investment)}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-600">Max Investment ROI</span>
                      <span className="font-semibold">{formatPercent(results.roi_projections?.baseline?.roi_percent_max_investment)}</span>
                    </div>
                  </div>
                </div>
                <div>
                  <h4 className="font-semibold text-gray-700 mb-4">With Growth Uplift</h4>
                  <div className="space-y-3">
                    <div className="flex justify-between">
                      <span className="text-gray-600">Projected Min ROI</span>
                      <span className="font-semibold text-green-600">{formatPercent(results.roi_projections?.with_uplift?.projected_roi_percent_min_investment)}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-600">Projected Max ROI</span>
                      <span className="font-semibold text-green-600">{formatPercent(results.roi_projections?.with_uplift?.projected_roi_percent_max_investment)}</span>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            {/* AI Investment Advice */}
            {results.llm_investment_advice && (
              <div className="bg-gradient-to-r from-blue-50 to-purple-50 rounded-xl border border-blue-200 p-8">
                <h3 className="text-xl font-bold text-gray-900 mb-6 flex items-center">
                  <Target className="w-6 h-6 mr-3 text-blue-600" />
                  AI Investment Recommendation
                </h3>

                <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
                  <div className="space-y-6">
                    <div>
                      <h4 className="font-semibold text-gray-700 mb-2">Investment Summary</h4>
                      <p className="text-gray-800 bg-white p-4 rounded-lg border">{results.llm_investment_advice.investment_summary}</p>
                    </div>

                    <div>
                      <h4 className="font-semibold text-gray-700 mb-2">Recommended Amount</h4>
                      <p className="text-2xl font-bold text-blue-600">{formatCurrency(results.llm_investment_advice.recommended_investment_amount)}</p>
                    </div>

                    <div>
                      <h4 className="font-semibold text-gray-700 mb-2">Expected Payback</h4>
                      <p className="text-lg font-semibold text-gray-800">{results.llm_investment_advice.expected_payback_months} months</p>
                    </div>

                    <div>
                      <h4 className="font-semibold text-gray-700 mb-2">Confidence Level</h4>
                      <p className="text-gray-800 bg-white p-3 rounded-lg border">{results.llm_investment_advice.confidence}</p>
                    </div>
                  </div>

                  <div className="space-y-6">
                    <div>
                      <h4 className="font-semibold text-gray-700 mb-2">Investment Strategy</h4>
                      <ul className="space-y-2">
                        {results.llm_investment_advice.high_level_strategy?.map((item, idx) => (
                          <li key={idx} className="flex items-start bg-white p-3 rounded-lg border">
                            <span className="w-2 h-2 bg-blue-500 rounded-full mt-2 mr-3 flex-shrink-0"></span>
                            {item}
                          </li>
                        ))}
                      </ul>
                    </div>

                    <div>
                      <h4 className="font-semibold text-gray-700 mb-2">Main Risks</h4>
                      <ul className="space-y-2">
                        {results.llm_investment_advice.main_risks?.map((risk, idx) => (
                          <li key={idx} className="flex items-start bg-white p-3 rounded-lg border">
                            <AlertTriangle className="w-4 h-4 text-red-500 mt-1 mr-3 flex-shrink-0" />
                            {risk}
                          </li>
                        ))}
                      </ul>
                    </div>
                  </div>
                </div>
              </div>
            )}

            {/* Download Report Button */}
            <div className="text-center">
              <button
                onClick={downloadReport}
                className="bg-gradient-to-r from-green-600 to-blue-600 text-white py-4 px-8 rounded-lg font-semibold text-lg hover:from-green-700 hover:to-blue-700 focus:ring-4 focus:ring-green-300 transition-all transform hover:scale-105 inline-flex items-center"
              >
                <Download className="w-6 h-6 mr-3" />
                Download Investment Report (PDF)
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default InvestorDashboard;
